import React, { Component } from "react";
import "./AdvisoryBoard.css";
import Faker from "faker";
export default class AdvisoryBoard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      national: [],
      international: [],
    };
  }

  componentWillMount() {
    for (let i = 0; i < 5; i++) {
      const person = {
        name: Faker.name.firstName(),
        designation: Faker.name.jobTitle(),
      };
      this.setState((prevState) => ({
        national: [...prevState.national, person],
      }));
    }

    for (let i = 0; i < 5; i++) {
      const person = {
        name: Faker.name.firstName(),
        designation: Faker.name.jobTitle(),
      };
      this.setState((prevState) => ({
        international: [...prevState.international, person],
      }));
    }
  }
  showNational() {
    var lis = [];
    for (var i in this.state.national) {
      lis.push(
        <li className="item">
          <h2 class="headline">{this.state.national[i].name}</h2>
          <span>{this.state.national[i].designation}</span>
        </li>
      );
    }
    return <ol className="list">{lis}</ol>;
  }
  showInternatioanl() {
    var lis = [];
    for (var i in this.state.international) {
      lis.push(
        <li className="item">
          <h2 class="headline">{this.state.international[i].name}</h2>
          <span>{this.state.international[i].designation}</span>
        </li>
      );
    }
    return <ol className="list">{lis}</ol>;
  }
  render() {
    return (
      <div>
        <h1>National</h1>
        {this.showNational()}
        <h1>Inter-National</h1>
        {this.showInternatioanl()}
      </div>
    );
  }
}
